import React from 'react';
import { capitalizeFirstLetter } from "../../utils/helpers"

function Nav() {
    const categories = [
        { name: "commercial", descritption: "Photos of grocery stores, food trucks, and other commercial projects" },
        { name: "portraits", description: "Portraits of people in my life" },
        { name: "food", description: "Delicious delecacies" },
        { name: "landscape", description: "Fields, farmhouses, waterfalls, and the beauty of nature" },
    ];

    const handleClick = (item) => {
        console.log(item)
        return item;
    }

    return (
        <header className="flex-row px-1">
            <h2>
                <a data-testid="link" href="/">
                    <span role="img" aria-label="camera">ðŸ“¸</span> Oh Snap!
                </a>
            </h2>
            <nav>
                <ul className="flex-row">
                    <li className="mx-2">
                        <a data-testid="about" href="#about" onClick={() => handleClick("About")}>
                            About Me
                        </a>
                    </li>
                    <li className="mx-2">
                        <span onClick={() => handleClick("Contact")}>
                        Contact
                        </span>
                    </li>
                    {categories.map((category) => {
                        <li className="mx-1" key={category.name} >
                        {/* use built in onClick method */}
                        <span onClick={() => { handleClick(category.name); }} >
                            {capitalizeFirstLetter(category.name)}
                        </span>
                    </li>
                    })}
                </ul>
            </nav>
        </header>
    );
}

export default Nav;
